mapper.select:
select bd_id, projectTitle, serialNumber, title, sort, bidingTime, bidingType, creatime, state, bidding, Remark, delflag, opt_id, optime from t_biddingdocument where delflag=0
and serialNumber like concat('%',#{s_roleName},'%')
order by optime desc limit #{pageBean.start},#{pageBean.rows}
