mapper.select:
select u.user_id, u.user_name, u.user_pwd, u.user_phone, u.user_idCard, u.role_id, u.state, u.delflag, u.createtime, u.remark, r.role_name from t_userinfo u, t_roleinfo r where u.delflag=0 and u.role_id=r.role_id
and u.user_name like concat('%',#{searchByName},'%')
order by u.createtime desc limit #{pageBean.start},#{pageBean.rows}
